import React, {useState} from 'react'
import { useForm } from "react-hook-form";

const Addproduct = ({onAdd}) => {
    const { register, handleSubmit, errors } = useForm();
    const onSubmit = data => console.log(data);
        // const [valueInput, setValue] = useState({});
        
        // const onHandleChange = (e)=>{
        //     const {name, value} = e.target;
        //     setValue({
        //         ...valueInput,
        //         [name]: value
        //     });
        // }
        const onHandleSubmit = (data) => {
            // e.preventDefault();
            // onAdd(valueInput);
            console.log(data);
        }
    
    return (
        <div className="card-body">
            <form onSubmit={handleSubmit(onHandleSubmit)}>
                <div className="form-group">
                    <label htmlFor="inputProductName">Ten san pham</label>
                    <input type="text" className="form-control col-md-6" name="name" id="inputProductName" ref={register({required:true})} />
                </div>
                <div className="form-group">
                    <label htmlFor="inputProductImage">Anh san pham</label>
                    <input type="file" className="form-control col-md-6" name="image" id="inputProductImage" ref={register({required:true})} />
                </div>
                <div className="form-group">
                    <label htmlFor="inputProductPrice">Gia san pham</label>
                    <input type="text" className="form-control col-md-6" name="price" id="inputProductPrice" ref={register({required:true})} />
                </div>
                <button type="submit" className="btn btn-success">Submit</button>
            </form>
        </div>
    )
}

export default Addproduct
