import React, { useState, useEffect } from 'react';
import dataFake from './dataFake';
import Routers from './routers'
import apiRequest from './api/productApi'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
// import AddProduct from './components/AddProduct';
function App() {

  const [products, setProducts] = useState([]);
  // const [status, setStatus] = useState(false);
  // const [color, setColor] = useState('green');
  // const onHandleClick = () => {
  //   // setStatus(true);
  //   setColor('red');
  // }
  // const onHandleRemove = (id) => {
  //   const comfirmRemove = window.confirm('Co xoa sp ko');
  //   const newProduct = products.filter(product => product.id !== id);
  //   if(comfirmRemove)setProducts(newProduct);
  // }
  // // const onHandleEdit = (id) => {
  // //   // const newProducts = products.filter(product => product.id !== id);
  // //   // setProducts(newProducts);
  // //   console.log(id)
  // // }
  // const onHandleAdd = (product) => {
  //   const newProduct = {
  //     id: products.length + 1,
  //     ...product
  //   }
  //   setProducts([
  //     ...products,
  //     newProduct
  //   ])
  // }
  // Danh sách sản phẩm
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);
  // xoa san pham
  const onHandleRemove = async(id) => {
    try {
      const { data } = await apiRequest.remove(id);
      // const comfirmRemove = window.confirm('Co xoa sp ko');
      console.log(products)
      const newProduct = products.filter(product => product.id !== data.id);
      console.log(newProduct);
      // if(comfirmRemove)setProducts(newProduct);
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }
  //them san pham
  const onHandleAdd = async(product) => {
    try{
      const { data } = await apiRequest.create(product);
      const newProduct = {
        id: data.length + 1,
        ...product
      }
      setProducts([
        ...products,
        newProduct
      ])
    }
  catch (error) {
    console.log('failed to request API: ', error)
  }
    }
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd} />
    </div>
  )

}
export default App;
