import React, {useState} from 'react'

const Addproduct = ({onAdd}) => {
        const [valueInput, setValue] = useState({});
        console.log(valueInput);
        const onHandleChange = (e)=>{
            const {name, value} = e.target;
            setValue({
                ...valueInput,
                [name]: value
            });
        }
        const onHandleSubmit = (e) => {
            e.preventDefault();
            onAdd(valueInput);
        }
    
    return (
        <div className="card-body">
            <form onSubmit={onHandleSubmit}>
                <div className="form-group">
                    <label htmlFor="inputProductName">Ten san pham</label>
                    <input type="text" className="form-control col-md-6" name="name" id="inputProductName" onChange={onHandleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="inputProductImage">Anh san pham</label>
                    <input type="file" className="form-control col-md-6" name="image" id="inputProductImage" onChange={onHandleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="inputProductPrice">Gia san pham</label>
                    <input type="text" className="form-control col-md-6" name="price" id="inputProductPrice" onChange={onHandleChange} />
                </div>
                <button type="submit" className="btn btn-success">Submit</button>
            </form>
        </div>
    )
}

export default Addproduct
